import './App.css';
import React from 'react';
import { Component } from 'react';
import Navbar from './components/NavBar'
import BarChart from './components/BarChart';
import Button from  './components/Button';
import Chart from './components/BarChart';
import { render } from '@testing-library/react';

class App extends Component {
  constructor(){
    super();
    this.state = {
      chartData : {}  
    };
  };

  componentWillMount(){
    this.getChartData();
  }

  ResetToRandom(){
    let arrayToSort = [500];
    for(let i=0; i<500; i++){
      let nextNum = Math.floor(Math.random() * 1000);
      arrayToSort[i] = nextNum;
    }
    return arrayToSort;
  }

 BubbleSort(arrayToSort){
    var swappedOnCurrentIter = false;
    var placeholder;

     for (let i=1; i<500; i++){
         if(arrayToSort[i-1]>arrayToSort[i]){
            placeholder = arrayToSort[i-1];
            arrayToSort[i-1] = arrayToSort[i];
            arrayToSort[i] = placeholder;
            swappedOnCurrentIter = true; 
         }
    }
    if(swappedOnCurrentIter == true){
        this.UpdateBarChart(arrayToSort);
        this.BubbleSort(arrayToSort);
    }
    else{

        return arrayToSort;
    }
}
  UpdateBarChart(currentArray){
    this.state.chartData.datasets.data = currentArray;
    <ul>
    {this.chartData.datasets.data.map(item => {
      return <li>{item[0]}</li>;
    })}
  </ul>
  }

  getChartData(){
    let labels = [500]
    for(let i=0; i<500; i++){
      labels[i] = '';
    }
    labels[0] = 'Lowest';
    labels[495] = 'Highest';
    let arrayToSort = this.ResetToRandom();
    this.setState({
      chartData: {
        labels: labels, 
        datasets:[
            {
                label: "Entries",
                data: arrayToSort
            }
        ],
        backgroundColor: 'purple', 
    }
    })


  }
  render(){
    return (
      <div className="App">
        <Navbar />
        <Chart chartData = {this.state.chartData}/> 
        <button onClick="BubbleSort(this.state.chartData.datasets.data)">BubbleSort</button>
      </div>
    );
    }
  }
export default App;
