import './App.css';
import React from 'react';
import { Component } from 'react';
import Navbar from './components/NavBar'
import Chart from './components/BarChart';


class App extends Component {
  constructor(){
    super();
    this.state = {
      chartData : {}  
    };
  };

  componentWillMount(){
    this.getChartData();
  }

  ResetToRandom(){
    let arrayToSort = [500];
    for(let i=0; i<500; i++){
      let nextNum = Math.floor(Math.random() * 1000);
      arrayToSort[i] = nextNum;
    }
    return arrayToSort;
  }

 BubbleSort(arrayToSort){
    var swappedOnCurrentIter = false;
    var placeholder;
    let newArray = arrayToSort;

     for (let i=1; i<500; i++){
         if(newArray[i-1]>newArray[i]){
            placeholder = newArray[i-1];
            newArray[i-1] = newArray[i];
            newArray[i] = placeholder;
            swappedOnCurrentIter = true; 
         }
    }
    if(swappedOnCurrentIter === true){
      //this.state.chartData.datasets[0].data = newArray;
      this.state.chartData.datasets[0].data.setState(newArray);
      this.UpdateBarChart(newArray);
      this.BubbleSort(newArray);
    }
    else{
        return arrayToSort;
    }
  }

  UpdateBarChart(currentArray){
    //this.state.chartData.datasets[0].data = currentArray; 
    this.setState({
      chartData: {
        labels: labels, 
        datasets:[
            {
                label: "Updated",
                data: currentArray
            }
        ],
        backgroundColor: 'purple', 
    }
    })
    //this.state.chartData.datasets[0].data.setState(currentArray); 
    this.setState(this.state.chartData);
    this.chartReference.changeData(this.state.chartData);

  }

  printArray(){
    let array = ["4","5","6"];
    return array;
  }

  getChartData(){
    let labels = [500]
    for(let i=0; i<500; i++){
      labels[i] = '';
    }
    labels[0] = 'Lowest';
    labels[495] = 'Highest';
    let arrayToSort = this.ResetToRandom();
    this.setState({
      chartData: {
        labels: labels, 
        datasets:[
            {
                label: "Entries",
                data: arrayToSort
            }
        ],
        backgroundColor: 'purple', 
    }
    })


  }


  chartReference = {};
  componentDidMount() {
    console.log(this.chartReference); // returns a Chart.js instance reference
  }

  render(){
    return (
      <div className="App">
        <Navbar />
        <Chart chartData = {this.state.chartData} ref={(reference) => this.chartReference = reference}/> 
        <textChange/>
        <button onClick= {() => {this.BubbleSort(this.state.chartData.datasets[0].data)}}>BubbleSortCLick</button>
        <h3>ChartData.data</h3> <p>{this.state.chartData.datasets[0].data}</p>
        <h3>print array: </h3> <p>{this.printArray()}</p>


      </div>
    );
    }
  }
  export default App;
