              width={'1400px'}
              height={'500px'}
              chartType="Bar"
              loader={<div>Loading Chart</div>}
              data={
                this.state.chartData
              }
              options={{
                // Material design options
                chart: {
                  title: 'Randomized Data',
                  subtitle: 'Select a sort button to sort the data visually.',
                  //backgroundColor: 'red',
                },
                colors: ['#7509b0'],
              }}
              // For tests
              rootProps={{ 'data-testid': '2' }}
            />
