import * as React from "react";
import { Chart } from "react-google-charts";


class GoogleChart extends React.Component{
    constructor(props){
        super(props);
        this.chartReference = React.createRef();
        this.state = {
            arrayToSort : [],
            chartData : {}  
        }

    }
    ResetToRandom(){
        let arrayToSort = [500];
        for(let i=0; i<500; i++){
          let nextNum = Math.floor(Math.random() * 1000);
          arrayToSort[i] = nextNum;
        }
        return arrayToSort;
      }

     BubbleSort(passedArrayToSort){
        let arrayToSort = passedArrayToSort
        var swappedOnCurrentIter = false;
        var placeholder;
        let newArray = arrayToSort;

         for (let i=1; i<500; i++){
             if(newArray[i-1]>newArray[i]){
                placeholder = newArray[i-1];
                newArray[i-1] = newArray[i];
                newArray[i] = placeholder;
                swappedOnCurrentIter = true; 
             }
        }
        if(swappedOnCurrentIter === true){
          this.UpdateBarChart(newArray);
          this.BubbleSort(newArray);
        }
        else{
            return arrayToSort;
        }
      }

      UpdateBarChart(currentArray){
        let displayArray = this.CreateDisplayArrayFromArrayToSort(currentArray);
        //this.state.chartData.datasets[0].data = currentArray; 
        this.setState({
            arrayToSort : currentArray,
            chartData: displayArray
          })
        //console.log("App State Data: "+ this.state.chartData.datasets[0].data);
        //this.changeData(this.state.chartData);

        //this.changeData(this.getChartData);
        //this.myGoogleChart.changeData(this.state.chartData)
        //myChart.changeData(this.state.chartData);
        //this.chartReference.chartInstance.update();
        }

      printArray(){
        let data=[
          ['Year', 'Sales', 'Expenses', 'Profit'],
          ['2014', 1000, 400, 200],
          ['2015', 1170, 460, 250],
          ['2016', 660, 1120, 300],
          ['2017', 1030, 540, 350],
        ];
        return data;
      }

      getChartData(){
        let arrayToSort = this.ResetToRandom();
        let displayArray = this.CreateDisplayArrayFromArrayToSort(arrayToSort);
        this.setState({ 
          arrayToSort: arrayToSort,
          chartData: displayArray
        })
      }

      CreateDisplayArrayFromArrayToSort(arrayToSort){
        let placeholderArray = [];
        let holdingArray = [500];
        holdingArray[0] = ['0-500','Entry Value'];
        for(let i=1; i<501;i++ ){
          placeholderArray[0] = i; 
          placeholderArray[1] = arrayToSort[i];
          holdingArray[i] = placeholderArray;
          placeholderArray = [];

        }
        return holdingArray;
      }

    changeData = (newDataState) => {
        this.myGoogleChart.setState({
            changeData: newDataState
        })
        this.setState({
            changeData: newDataState
        })
    }

    render() {
        return (
          <div className={"my-pretty-chart-container"}>
            <h1>{this.state.chartData}</h1>
            <Chart
              width={'800px'}
              height={'400px'}
              chartType="Bar"
              loader={<div>Loading Chart</div>}
              data={
                this.state.chartData
              }
              options={{
                // Material design options
                chart: {
                  title: 'Company Performance',
                  subtitle: 'Sales, Expenses, and Profit: 2014-2017',
                },
              }}
              // For tests
              rootProps={{ 'data-testid': '2' }}
            />
            <button onClick= {() => {this.BubbleSort(this.state.arrayToSort)}}>BubbleSortCLick</button>
          </div>
        );
      }
}export default GoogleChart;
