import './App.css';
import React from 'react';
import { Component } from 'react';
import Navbar from './components/NavBar'
import Chart from './components/BarChart';
import GoogleChart from './components/GoogleChart';


class App extends Component {
  constructor(){
    super();
    this.state = {
      chartData : {}  
    };
  };

  UNSAFE_componentWillMount(){
    this.getChartData();
  }

  ResetToRandom(){
    let arrayToSort = [500];
    for(let i=0; i<500; i++){
      let nextNum = Math.floor(Math.random() * 1000);
      arrayToSort[i] = nextNum;
    }
    return arrayToSort;
  }

 BubbleSort(passedArrayToSort){
    let arrayToSort = passedArrayToSort
    var swappedOnCurrentIter = false;
    var placeholder;
    let newArray = arrayToSort;

     for (let i=1; i<500; i++){
         if(newArray[i-1]>newArray[i]){
            placeholder = newArray[i-1];
            newArray[i-1] = newArray[i];
            newArray[i] = placeholder;
            swappedOnCurrentIter = true; 
         }
    }
    if(swappedOnCurrentIter === true){
      this.UpdateBarChart(newArray);
      this.BubbleSort(newArray);
    }
    else{
        return arrayToSort;
    }
  }

  UpdateBarChart(currentArray){

    //this.state.chartData.datasets[0].data = currentArray; 
    this.setState({
        chartData: currentArray
      })
    //console.log("App State Data: "+ this.state.chartData.datasets[0].data);
    //this.changeData(this.state.chartData);

    //this.chartReference.changeData(this.state.chartData);

    //this.chartReference.chartInstance.update();
    }

  printArray(){
    let data=[
      ['Year', 'Sales', 'Expenses', 'Profit'],
      ['2014', 1000, 400, 200],
      ['2015', 1170, 460, 250],
      ['2016', 660, 1120, 300],
      ['2017', 1030, 540, 350],
    ];
    return data;
  }

  getChartData(){
    let arrayToSort = this.ResetToRandom();
    let displayArray = this.CreateDisplayArrayFromArrayToSort(arrayToSort);
    this.setState({ 
      chartData: displayArray
    })
  }

  CreateDisplayArrayFromArrayToSort(arrayToSort){
    let placeholderArray = [];
    let holdingArray = [500];
    holdingArray[0] = ['Entries','Columns'];
    for(let i=1; i<501;i++ ){
      placeholderArray[0] = i; 
      placeholderArray[1] = arrayToSort[i];
      holdingArray[i] = placeholderArray;
      placeholderArray = [];

    }
    console.log("Holding array: ", holdingArray);


    return holdingArray;
  }

  render(){
    return (
      <div className="App">
        <Navbar />
        <GoogleChart chartData = {this.state.chartData}/>
        <button onClick= {() => {this.BubbleSort(this.state.chartData)}}>BubbleSortCLick</button>
        <h3>ChartData.data: </h3> <p>{this.state.chartData}</p>  
        <h3>Actual Data: </h3> <p>{this.printArray()}</p> 
      </div>
    );
    }
  }
  export default App;
