import React from 'react'
import {Bar} from 'react-chartjs-2'
import Button from './Button'

const Barchart = () => {
    let arrayToSort = [500];
    let labels = [500]
    for(let i=0; i<500; i++){
        let nextNum = Math.floor(Math.random() * 1000);
        arrayToSort[i] = nextNum;
        labels[i] = ''
    }
    labels[0] = 'Lowest'
    labels[495] = 'Highest'
    return(
        <div>
            <Bar
            data = {{
                labels: labels, 
                datasets: [
                    {
                        label:'A randomly generated number entry',
                        data: arrayToSort,
                        backgroundColor: 'purple'
                    },
                ],
            }}
            height={400}
            width ={600}
            options={{
                skipLabels : true,
                maintainAspectRatio : false,
                scales: {
                    yAxes: [
                        {
                        ticks: {
                            beginAtZero : true,
                        }, 
                    }    
                ],
            },
        }}
        />
        </div>
    )
}

function ReturnArrayToSort(){
    return arrayToSort
}
export default Barchart
